function loadWeather(a,b){$.simpleWeather({location:a,woeid:b,unit:"c",success:function(a){html='<h2><i class="icon-'+a.code+'"></i> '+a.temp+"&deg;"+a.units.temp+"</h2>",html+="<ul><li>"+a.city+", "+a.region+"</li>",html+='<li class="currently">'+a.currently+"</li>",html+="<li>"+a.alt.temp+"&deg;F</li></ul>",$("#weather").html(html)},error:function(a){$("#weather").html('<p class="error">'+a+"</p>")}})}$(document).ready(function(){loadWeather("Hamburg","2345484")});