function loadWeather(a,b){var c="";$.simpleWeather({location:a,woeid:b,unit:"c",success:function(a){html='<h2><i class="icon-'+a.code+'"></i> '+a.temp+"&deg;"+a.units.temp+"</h2>",html+='<ul class="forecast-today">',html+="<li>"+a.city+"</li>",html+="<li>"+a.currently+"</li>",html+="<li>"+a.alt.temp+"&deg;F</li>",html+="</ul>";for(var b=1;b<a.forecast.length;b++)c=1===b?"Tomorrow":a.forecast[b].day,html+='<ul class="small forecast-week">',html+="<li>"+c+": "+a.forecast[b].high+"&deg;"+a.units.temp+"</li>",html+="</ul>";$("#weather").html(html)},error:function(a){$("#weather").html('<p class="error">'+a+"</p>")}})}function datetime(){var a=moment(),b=document.getElementById("datetime"),c="";c+='<p class="time">'+a.format(getTimeFormat())+"</p>",c+='<p class="date">'+a.format("dddd, MMMM Do YYYY")+"</p>",b.innerHTML=c}function getTimeFormat(){return 24==config.timeFormat?"HH:mm":"h:mma"}var config={location:"Hamburg",woeid:2345484,timeFormat:24,greeting:"Hello"};$(document).ready(function(){loadWeather(config.location,config.woeid),datetime(),setInterval(datetime,1e3)});